mapper.select:
SELECT C.HIDX, C.HPNAME, C.HPADDR, C.HPTEL, C.CLASSCODE, C.AGENCY, C.WGS84LON, C.WGS84LAT, C.CLASSNAME FROM ( SELECT A.HIDX, A.HPNAME, A.HPADDR, A.HPTEL, A.CLASSCODE, A.AGENCY, A.WGS84LON, A.WGS84LAT, B.CLASSNAME, ROW_NUMBER() OVER (ORDER BY A.HIDX DESC) AS RNO FROM ( SELECT MIN(H.HIDX) AS HIDX, H.HPNAME, H.HPADDR, MAX(H.HPTEL) AS HPTEL, MAX(H.CLASSCODE) AS CLASSCODE, MAX(H.AGENCY) AS AGENCY, MAX(H.WGS84LON) AS WGS84LON, MAX(H.WGS84LAT) AS WGS84LAT FROM hospital H WHERE H.hpaddr LIKE '%' || #{addr} || '%' AND H.hpaddr LIKE '%' || #{country} || '%'
AND H.classcode = #{classcode}
AND H.agency = #{agency}
AND H.hpname LIKE '%' || #{hpname} || '%'
GROUP BY H.HPNAME, H.HPADDR ) A JOIN CLASSCODE B ON A.CLASSCODE = B.CLASSCODE ) C WHERE C.RNO BETWEEN #{startRno} AND #{endRno}

SELECT count(*) FROM ( SELECT MIN(HIDX) AS HIDX, HPNAME, HPADDR, MAX(HPTEL) AS HPTEL, MAX(CLASSCODE) AS CLASSCODE, MAX(AGENCY) AS AGENCY, MAX(WGS84LON) AS WGS84LON, MAX(WGS84LAT) AS WGS84LAT FROM hospital WHERE hpaddr LIKE '%' || #{addr} || '%' AND hpaddr LIKE '%' || #{country} || '%'
AND classcode = #{classcode}
AND agency = #{agency}
AND hpname LIKE '%' || #{hpname} || '%'
GROUP BY HPNAME, HPADDR, agency, classcode)
