mapper.update:
UPDATE MEMBER
SET mobile = #{mobile}

	,address = #{address}, detailaddress = #{detailaddress}

WHERE USERID = #{userid}

mapper.select:
select userid
from MEMBER 
where userid = #{userid} and PWD = #{pwd}

mapper.update:
UPDATE MEMBER
SET pwd = #{pwd}, PWDUPDATEDAY = default
WHERE userid = #{userid}

mapper.select:
SELECT ridx, userid, reportday, checkin, rcode, hidx FROM ( SELECT row_number() over(order by ridx desc) as rno , ridx, RM.userid, reportday, checkin, RM.rcode, hidx FROM ( SELECT ridx, M.userid, reportday, checkin, rcode, hidx FROM ( select ridx, userid, reportday, checkin, rcode, hidx from reserve where hidx = #{hidx} order by checkin desc )R JOIN ( select userid, name from member
where name like '%' || #{inputsc} || '%'
)M ON R.userid = M.userid ) RM JOIN ( select rcode, rstatus from reservecode
where rstatus = #{inputsc}
) RC ON RM.rcode = RC.rcode ) WHERE rno between #{startRno} and #{endRno}

SELECT count(*) FROM ( SELECT row_number() over(order by ridx desc) as rno , ridx, RM.userid, reportday, checkin, RM.rcode, hidx FROM ( SELECT ridx, M.userid, reportday, checkin, rcode, hidx FROM ( select ridx, userid, reportday, checkin, rcode, hidx from reserve where hidx = #{hidx} order by checkin desc )R JOIN ( select userid, name from member
where name like '%' || #{inputsc} || '%'
)M ON R.userid = M.userid ) RM JOIN ( select rcode, rstatus from reservecode
where rstatus = #{inputsc}
) RC ON RM.rcode = RC.rcode )

select name, mobile
from member
where userid = #{patient_id}

select rcode
from reservecode 
where rstatus = #{rStatus}

SELECT ridx, userid, reportday, checkin, rcode, hidx FROM ( SELECT row_number() over(order by ridx desc) as rno , ridx, userid, reportday, checkin, rcode, H.hidx as hidx FROM ( SELECT ridx, RM.userid, reportday, checkin, RM.rcode, hidx, rstatus FROM ( SELECT ridx, R.userid, reportday, checkin, rcode, hidx FROM ( select ridx, userid, reportday, checkin, rcode, hidx from reserve ) R JOIN ( select userid from member where userid = #{userid} )M ON R.userid = M.userid )RM JOIN ( select rcode, rstatus from reservecode
where rstatus = #{inputsc}
)RC ON RM.rcode = RC.rcode ) RMC JOIN ( select hidx, hpname, hptel from hospital
where hpname like '%' || #{inputsc} || '%'
) H ON RMC.hidx = H.hidx ) WHERE rno between #{startRno} and #{endRno}

SELECT count(*) FROM ( SELECT row_number() over(order by ridx desc) as rno , ridx, userid, reportday, checkin, rcode, H.hidx as hidx FROM ( SELECT ridx, RM.userid, reportday, checkin, RM.rcode, hidx, rstatus FROM ( SELECT ridx, R.userid, reportday, checkin, rcode, hidx FROM ( select ridx, userid, reportday, checkin, rcode, hidx from reserve ) R JOIN ( select userid from member where userid = #{userid} )M ON R.userid = M.userid )RM JOIN ( select rcode, rstatus from reservecode
where rstatus = #{inputsc}
)RC ON RM.rcode = RC.rcode ) RMC JOIN ( select hidx, hpname, hptel from hospital
where hpname like '%' || #{inputsc} || '%'
) H ON RMC.hidx = H.hidx )

SELECT userid, pwd, email, name, address, detailAddress, birthday, mobile, gender, mIdx, registerday, pwdUpdateday, loginmethod FROM member WHERE 1=1
AND userid LIKE CONCAT('%', #{userid}, '%')
AND loginmethod = #{mbr_division}
