mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY registerday DESC) AS rn, userid, pwd, email, name, address, detailAddress, birthday, mobile, gender, mIdx, registerday, pwdUpdateday, loginmethod, (SELECT MAX(registerday) FROM loginlog WHERE userid = m.userid) as lastLogin, (SELECT COUNT(*) FROM mediQ WHERE userid = m.userid) as postcount FROM member m WHERE 1=1 AND userid != 'Anonymous'
AND (name LIKE '%' || #{word} || '%' OR userid LIKE '%' || #{word} || '%')
AND name LIKE '%' || #{word} || '%'
AND (name LIKE '%' || #{word} || '%' OR userid LIKE '%' || #{word} || '%')
) numbered_members WHERE rn BETWEEN #{start} AND #{end}
